{
        "class": "Cloud_Failover",
        "environment": "<SET YOUR CLOUD PROVIDER HERE. possible values: azure | aws | gcp>",
        "externalStorage": {
            				"scopingTags": {
                							"f5_cloud_failover_label": "MyDeployment"
            								}
        					},
        "failoverAddresses": {
            				"scopingTags": {
                							"f5_cloud_failover_label": "MyDeployment"
            								}
        						},
        "failoverRoutes": {
            				"scopingTags": {
                							"f5_cloud_failover_label": "MyDeployment"
            								},
            				"scopingAddressRanges": [
	            									{
	                    								"range": "10.0.2.0/24"
		                							}
            										],
            				"defaultNextHopAddresses": {
                										"discoveryType": "static",
                										"items": [
                    												"10.0.2.4",
                    												"10.0.2.5"
                													]
            											}
        					},
        	"controls": {
        				"class": "Controls",
        				"logLevel": "silly"
	    				}

}

